// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "library/ReentrancyGuard.sol";

interface IERC20 {
  function balanceOf(address account) external view returns (uint256);
}

interface ICCTPAdapter{
    function transferRemote(
        uint32 _destinationDomain,
        bytes32 _recipientAddress,
        uint256 _amount
    ) external payable returns (bytes32 messageId);
}


contract USDCTransferCengli is ReentrancyGuard{
    address public USDCAddress;
    address public CCTPAdapterAddress;

    constructor(address _USDCAddress, address _CCTPAdapterAddress){
        USDCAddress = _USDCAddress;
        CCTPAdapterAddress = _CCTPAdapterAddress;
    }

    function checkBalance(address _userAddress, address _tokenAddress) external view returns (uint256) {
        uint256 balance = IERC20(_tokenAddress).balanceOf(_userAddress);
        return balance;
    }

    function transferXchainUSDC(uint32 _destinationDomain, address _recipientAddress, uint256 _amount) nonReentrant external{
        ICCTPAdapter(CCTPAdapterAddress).transferRemote(_destinationDomain, bytes32(bytes20(_recipientAddress)), _amount);
    }
}

